    n, k = map(int, input().split())
    s = input()

    if k > 2**n:
        return -1

    dp = [[0] * k for _ in range(n + 1)]

    for i in range(n + 1):
        for j in range(1, k + 1):
            if i == 0:
                dp[i][j] = 0
            elif j == 1:
                dp[i][j] = i
            else:
                dp[i][j] = dp[i - 1][j]
                for x in range(i):
                    if dp[x][j - 1] > dp[i - x - 1][j]:
                        dp[i][j] = dp[x][j - 1] + i - x - 1
                        break

    return dp[n][k]

