            tiles = []
            for _ in range(2 * n):
                top_left, top_right, bottom_left, bottom_right = map(int, input().split())
                tiles.append((top_left, top_right, bottom_left, bottom_right))
            yield n, m, tiles, m

    def is_valid(matrix: [[int]], m: int) -> bool:
        for i in range(m):
            for j in range(m):
                if matrix[i][j] != matrix[j][i]:
                    return False
        return True

    def can_construct(n: int, m: int, tiles: [(int, int, int, int)], m: int) -> bool:
        def is_free(i: int, j: int) -> bool:
            for x in range(max(i - 1, 0), min(i + 2, m)):
                for y in range(max(j - 1, 0), min(j + 2, m)):
                    if matrix[x][y] != 0:
                        return False
            return True

        matrix = [[0] * m for _ in range(m)]
        for top_left, top_right, bottom_left, bottom_right in tiles:
            for i in range(m):
                for j in range(m):
                    if matrix[i][j] == 0 and (top_left == matrix[i][j - 1] and bottom_right == matrix[i + 1][j] or top_right == matrix[i - 1][j] and bottom_left == matrix[i][j + 1]):
                        matrix[i][j] = top_left
                        if is_free(i, j):
                            return True
        return False

    for n, m, tiles, m in read_input():
