            for j in range(n):
                matrix[i][j] = numbers.pop(0)
        return matrix

    matrix = create_matrix(numbers)

    if check_palindrome(matrix):
        return "YES\n" + "\n".join(map(lambda x: " ".join(map(str, x)), matrix))
    else:
