    projects = [list(map(int, line.split())) for line in input_data[1:]]

    def dfs(projects, rating, visited, path):
        if len(path) == n:
            return True
        if rating < 0:
            return False

        for i, (required_rating, change) in enumerate(projects):
            if visited[i]:
                continue
            if rating >= required_rating:
                visited[i] = True
                if dfs(projects, rating + change, visited, path + [i]):
                    return True
                visited[i] = False

        return False

    return "YES" if dfs(projects, r, [False] * n, []) else "NO"

