def solution(stdin: str) -> str:
    n, d, k = map(int, stdin.split())
    if n < 2 or d < 1 or k < 1 or n > 4 * 10**5 or d > 4 * 10**5 or k > 4 * 10**5:
        return "NO"

    # Base case: build a tree with 1 vertex
    if n == 1:
        return "YES\n"

    # Base case: build a tree with 2 vertices
    if n == 2:
        return "YES\n" + str(1) + " " + str(2) + "\n"

    # Check if the given conditions can be satisfied
    if d > n - 1 or 2 * k > n - 1 or (n - 1) % (2 * k - 1) != 0:
        return "NO"

    # Construct a tree using the given conditions
    tree = [[] for _ in range(n)]
    visited = [False] * n
    for i in range(1, n):
        tree[i].append(i - 1)
        tree[i - 1].append(i)
    visited[0] = True
    visited[1] = True

    # Perform a BFS to find a path of length d
    queue = [(0, 1)]
    while queue:
        node, depth = queue.pop(0)
        for neighbor in tree[node]:
            if not visited[neighbor]:
                visited[neighbor] = True
                if depth < d:
                    queue.append((neighbor, depth + 1))
                else:
                    return "NO"

    # Construct the tree using the found path
    tree_edges = []
    visited = [False] * n
    for i in range(2, n):
        for j in range(i):
            if not visited[j]:
                visited[j] = True
                tree_edges.append((j + 1, i + 1))
                break

    # Check if the degree of each vertex is at most k
    for i in range(1, n + 1):
        if len(tree[i - 1]) > k:
            return "NO"

    return "YES\n" + "\n".join(map(str, tree_edges))

