        if len(seq) <= 2:
            return True
        diff = seq[1] - seq[0]
        for i in range(2, len(seq)):
            if seq[i] - seq[i - 1] != diff:
                return False
        return True

    def count_changes_to_make_arithmetic_progression(seq):
        if is_arithmetic_progression(seq):
            return 0
        if seq[0] == 0:
            return -1
        if seq[0] > 0 and is_arithmetic_progression([x - 1 for x in seq]):
            return 0
        if seq[-1] == 0:
            return -1
        if seq[-1] > 0 and is_arithmetic_progression([x + 1 for x in seq]):
            return 0
        for i in range(1, len(seq) - 1):
            if seq[i - 1] == 0 and is_arithmetic_progression([x + 1 for x in seq[i:]]):
                return i
            if seq[i] == 0 and is_arithmetic_progression([x - 1 for x in seq[:i + 1]]):
                return i
            if seq[i - 1] > 0 and seq[i] < seq[i - 1] - 1 and is_arithmetic_progression([x - 1 for x in seq[:i]] + [x + 2 for x in seq[i:]]):
                return i
            if seq[i - 1] < seq[i] + 1 and seq[i] < seq[-1] and is_arithmetic_progression([x - 1 for x in seq[:i]] + [x + 2 for x in seq[i:]]):
                return i
        return -1

    result = count_changes_to_make_arithmetic_progression(b)
    if result == -1:
        return -1
    else:
        return n - result

