    def is_valid_star(x, y, size):
        for dx, dy in [(0, 0), (0, -size), (-size, 0), (size, 0), (-size, size), (size, -size), (-size, -size), (size, size)]:
            if x + dx < 1 or x + dx > n or y + dy < 1 or y + dy > m:
                return False
            if grid[x + dx - 1][y + dy - 1] != '*':
                return False
        return True

    stars = []
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            if grid[i - 1][j - 1] == '*':
                for size in range(1, n + m + 1):
                    if is_valid_star(i, j, size):
                        stars.append((i, j, size))
                        break

    if not stars:
        return "-1"
    else:
        return len(stars), "\n".join(f"{x} {y} {s}" for x, y, s in stars)

