    if min_k == 0:
        return "0\n"

    # Find the optimal operation sequence
    while min_k != 0:
        k += 1
        for i in range(n):
            for j in range(i + 1, n):
                if abs(i - j) == 1:
                    diff = abs(a[i] - a[j])
                    if diff == min_k:
                        op_sequence.append((1, i, j))
                        a[i] += diff
                        a[j] -= diff
                        min_k = float('inf')
                        for p in range(n):
                            for q in range(p + 1, n):
                                if abs(p - q) == 1:
                                    min_k = min(min_k, abs(a[p] - a[q]))
                        break
                    elif diff < min_k:
                        min_k = diff

    # Print the optimal operation sequence
    result = [str(k)]
    for op in op_sequence:
        result.append(f"{op[0]} {op[1]} {op[2]}")
