    segments.sort()

    def intersect(a, b):
        return max(a[0], b[0]), min(a[1], b[1])

    bad_points = set()
    for i in range(n):
        for j in range(i + 1, n):
            a, b = intersect(segments[i], segments[j])
            if a <= b:
                bad_points.add(a)
                bad_points.add(b)

    def count_overlaps(x):
        count = 0
        for segment in segments:
            if segment[0] <= x <= segment[1]:
                count += 1
        return count

    bad_points = sorted(list(bad_points))
    bad_points_count = [count_overlaps(x) for x in bad_points]
    bad_points_count = [x for x in bad_points_count if x > k]
    bad_points_count.sort(reverse=True)

