    def kruskal(n: int, edges: list) -> str:
        edges.sort(key=lambda x: x[2])
        parent = list(range(n))
        rank = [0] * n

        result = []
        for edge in edges:
            v, u, _ = edge
            if find(parent, v) != find(parent, u):
                result.append(edge)
                union(parent, rank, v, u)
                if len(result) == n - 1:
                    break

        return result

    def find(parent: list, i: int) -> int:
        if parent[i] != i:
            parent[i] = find(parent, parent[i])
        return parent[i]

    def union(parent: list, rank: list, v: int, u: int):
        v_root = find(parent, v)
        u_root = find(parent, u)
        if v_root == u_root:
            return
        if rank[v_root] < rank[u_root]:
            parent[v_root] = u_root
        elif rank[v_root] > rank[u_root]:
            parent[u_root] = v_root
        else:
            parent[u_root] = v_root
            rank[u_root] += 1

    n, m = map(int, stdin.split()[:2])
    edges = [tuple(map(int, stdin.split()[i:i + 3])) for i in range(1, m * 3, 3)]

    return "\n".join(str(e) for e in kruskal(n, edges))

