        if level == n:
            return visit_points(level_points)

        min_dist = float('inf')
        for i, p in enumerate(points):
            if not visited[i]:
                visited[i] = True
                level_points.append(p)
                dist = dfs(points, level + 1, level_points)
                if dist < min_dist:
                    min_dist = dist
                visited[i] = False
                level_points.pop()
        return min_dist

    key_points.sort(key=lambda x: max(abs(x[0]), abs(x[1])))
