    def find_kth_smallest_path(n: int, m: int, k: int, edges: list) -> int:
        adj_list = defaultdict(list)
        for edge in edges:
            adj_list[edge[0]].append((edge[1], edge[2]))
            adj_list[edge[1]].append((edge[0], edge[2]))

        distance = [float('inf')] * (n * (n - 1) // 2)
        visited = [False] * n
        visited[0] = True

        def dfs(node: int, parent: int, weight: int):
            if parent >= 0:
                distance[n * node + parent] = weight

            for neighbor, neighbor_weight in adj_list[node]:
                if not visited[neighbor]:
                    visited[neighbor] = True
                    dfs(neighbor, node, weight + neighbor_weight)

        dfs(0, -1, 0)
        heapify(distance)

        for i in range(k):
            heappop(distance)

        return distance[0]

