def solution(stdin: str) -> str:
    n = int(stdin.split('\n')[0])
    a, b = stdin.split('\n')[1:3]

    def swap(i, j):
        nonlocal a, b
        a = a[:i] + b[i] + a[i+1:]
        b = b[:j] + a[j] + b[j+1:]

    def preprocess(i, c):
        nonlocal a
        a = a[:i] + c + a[i+1:]

    for i in range(n):
        if a[i] != b[i]:
            for j in range(n):
                if b[j] == a[i]:
                    swap(i, j)
                    break
            else:
                return 0

    preprocess_count = 0
    for i in range(n//2):
        if a[i] != b[n-i-1]:
            preprocess(i, b[n-i-1])
            preprocess_count += 1
        elif a[n-i-1] != b[i]:
            preprocess(n-i-1, b[i])
            preprocess_count += 1

    for i in range(n//2):
        if a[i] != b[i]:
            preprocess(i, a[i])
            preprocess_count += 1
        elif a[n-i-1] != b[n-i-1]:
            preprocess(n-i-1, a[n-i-1])
