    for u, v in edges:
        adj[u - 1].append(v - 1)
        adj[v - 1].append(u - 1)

    def dfs(u, depth, parent):
        for v in adj[u]:
            if v != parent:
                dfs(v, depth + 1, u)

    for i in range(n):
        if not adj[i]:
            dfs(i, 0, -1)
            break

    def is_reachable(u, v):
        visited = [False] * n
        stack = [u]
        while stack:
            u = stack.pop()
            if u == v:
                return True
            visited[u] = True
            for v in adj[u]:
                if not visited[v]:
                    stack.append(v)
        return False

    result = 0
    for u in range(n):
        for v in range(n):
            if u != v and not is_reachable(u, v):
                result += 1

    return str(result)

