        return (i // 3) * 3 + (j // 3)

    def check_anti_sudoku(field: list[list[int]]) -> bool:
        for i in range(9):
            if len(set(field[i])) < 9:
                return False
            if len(set([field[x][i] for x in range(9)])) < 9:
                return False
        for i in range(3):
            for j in range(3):
                if len(set([field[i * 3 + x][j * 3 + y] for x in range(3) for y in range(3)])) < 9:
                    return False
        return True

    def is_valid(field: list[list[int]], i: int, j: int, value: int) -> bool:
        for x in range(9):
            if field[i][x] == value:
                return False
            if field[x][j] == value:
                return False
            block_i, block_j = get_block(i, x), get_block(x, j)
            if field[block_i][block_j] == value:
                return False
        return True

    def backtrack(field: list[list[int]]) -> None:
        changed = 0
        for i in range(9):
            for j in range(9):
                if field[i][j] == 0:
                    for value in range(1, 10):
                        if is_valid(field, i, j, value):
                            field[i][j] = value
                            if not check_anti_sudoku(field):
                                field[i][j] = 0
                                continue
                            changed += 1
                            if changed == 9:
                                return True
                            if backtrack(field):
                                return True
                            field[i][j] = 0
                            changed -= 1
                    return False
        return True

