            if size == max_size:
                return
            if i == n:
                nonlocal answer
                answer = max(answer, size)
                return
            if visited & (1 << i):
                dfs(i + 1, visited, size)
            else:
                dfs(i + 1, visited | (1 << i), size + 1)
                for j in range(i + 1, n):
                    if visited & (1 << j):
                        continue
                    if not is_non_intersecting(segments[i][0], segments[i][1], segments[j][0], segments[j][1]) and not is_inside(segments[i][0], segments[i][1], segments[j][0], segments[j][1]):
                        break
                dfs(i + 1, visited, size)

        answer = 0
        max_size = 1
        for i in range(n):
            max_size += 1
            dfs(0, 0, 0)
            max_size -= 1

        return answer

