        middle_parts = list(map(int, input_lines[2].split()))
        lower_parts = list(map(int, input_lines[3].split()))
        return n, upper_parts, middle_parts, lower_parts

    n, upper_parts, middle_parts, lower_parts = read_input(stdin)
    total_altars = count_altars(upper_parts, middle_parts, lower_parts)
