    def is_good(array):
        parities = [a % 2 for a in array]
        for i, parity in enumerate(parities):
            if i % 2 != parity:
                return False
        return True

    def make_good(array):
        if is_good(array):
            return 0

        min_moves = float('inf')
        for i in range(len(array)):
            if array[i] % 2 != i % 2:
                for j in range(i + 1, len(array)):
                    if array[j] % 2 == i % 2:
                        temp = array[i]
                        array[i] = array[j]
                        array[j] = temp
                        min_moves = min(min_moves, 1 + make_good(array))
                        temp = array[i]
                        array[i] = array[j]
                        array[j] = temp
        return min_moves

    def main(t: int, n: int, array: List[int]):
        result = make_good(array)
        if result == float('inf'):
            return -1
        return result

