                if current_sum == target_sum:
                    segments.append(current_segment)
                    current_segment += 1
                    return segments

            return []

        if not is_possible(a, k, target_sum):
            result.append("NO")
        else:
            result.append("YES")
            segments = is_possible(a, k, target_sum)
            result.append(str(segments)[1:-1].replace(",", " "))

