        return s1[end_idx - max_len: end_idx]

    def longest_common_substring_all(s: str, strings: List[str]) -> str:
        max_len = 0
        max_str = ""
        for t in strings:
            lcs = longest_common_substring(s, t)
            if len(lcs) > max_len:
                max_len = len(lcs)
