    def is_valid_position(x, y):
        return 0 <= x < 4 and 0 <= y < 4

    def find_empty_position(state, direction):
        for i in range(4):
            for j in range(4):
                if state[i][j] == 0 and is_valid_position(i + move_tile(1, direction)[0], j + move_tile(1, direction)[1]):
                    return i, j
        return -1, -1

    def move_tiles(state, direction):
        for _ in range(4):
            for i in range(4):
                for j in range(4):
                    if state[i][j] != 0:
                        new_i, new_j = i + move_tile(state[i][j], direction)[0], j + move_tile(state[i][j], direction)[1]
                        if is_valid_position(new_i, new_j) and state[new_i][new_j] == 0:
                            state[new_i][new_j], state[i][j] = state[i][j], 0
            for i in range(4):
                for j in range(4):
                    if state[i][j] != 0:
                        new_i, new_j = i + move_tile(state[i][j], direction)[0], j + move_tile(state[i][j], direction)[1]
                        if is_valid_position(new_i, new_j) and state[new_i][new_j] != 0:
                            state[i][j], state[new_i][new_j] = merge_tile(state[new_i][new_j], state[i][j])
            for i in range(4):
