                        if is_valid_position(new_i, new_j) and state[new_i][new_j] == 0:
                            state[new_i][new_j], state[i][j] = state[i][j], 0

    def merge_tiles(state):
        for i in range(4):
            for j in range(4):
                if state[i][j] != 0:
                    for k in range(i + 1, 4):
                        if state[k][j] == 0 and state[i][j] == state[k][j]:
