                j = i + 1
                while j < len(molecule) and molecule[j].isalpha():
                    j += 1
                result.append(molecule[i:j])
                i = j
            else:
                result.append(molecule[i])
                i += 1
        return result

    def count_molecules(molecules: List[str], molecule: str) -> int:
        count = 0
        for molecule_element in molecules:
            if molecule_element == molecule:
                count += 1
        return count

    input_molecule_parsed = parse_molecule(input_molecule)
    output_molecule_parsed = parse_molecule(output_molecule)

    input_molecules = input_molecule_parsed * input_count
    output_molecules = []

