        else:
            return word

    def add_vowel(word: str) -> str:
        last_consonant_index = max(word.rfind('b'), word.rfind('c'), word.rfind('d'), word.rfind('g'), word.rfind('k'), word.rfind('n'), word.rfind('p'), word.rfind('t'))
        if word[last_consonant_index + 1:].isupper() or word.endswith('-'):
            return word[:last_consonant_index + 1] + 'uh' + word[last_consonant_index + 1:]
        elif word.endswith('a'):
            return word[:last_consonant_index + 1] + 'ah' + word[last_consonant_index + 1:]
        else:
            return word[:last_consonant_index + 1] + 'oh' + word[last_consonant_index + 1:]

