    matrix = [[0 for _ in range(n)] for _ in range(n)]

    for i in range(n):
        for j in range(i + 1, n):
            count = 0
            for p in range(k):
                if samples[i][p] != samples[j][p]:
                    count += 1
            matrix[i][j], matrix[j][i] = count, count

    unlikeliness = [[0 for _ in range(n)] for _ in range(n)]
    for i in range(n):
        unlikeliness[i][i] = sum(matrix[i])
        for j in range(i + 1, n):
            unlikeliness[i][j] = unlikeliness[i][j - 1] + matrix[i][j]
            unlikeliness[j][i] = unlikeliness[j][i - 1] + matrix[j][i]

    min_unlikeliness = min(min(row) for row in unlikeliness)
    min_unlikeliness_index = 0
    for i in range(n):
        if unlikeliness[i][i] == min_unlikeliness:
            min_unlikeliness_index = i
            break

    result = [str(min_unlikeliness)]
    for i in range(n - 1):
        result.append(f"{min_unlikeliness_index} {(min_unlikeliness_index + i + 1) % n}")

