    output = []
    def is_appealing(pattern):
        n, m = pattern
        for i in range(1, n):
            if abs(pattern[i][1] - pattern[i-1][1]) > 1:
                return False
            if pattern[i][0] != pattern[i-1][0] and pattern[i][1] > pattern[i-1][1]:
                return False
            if pattern[i][0] != pattern[i-1][0] and pattern[i][1] < pattern[i-1][1]:
                return False
        return True

    def generate_patterns(n):
        if n == 1:
            yield (1, 1)
            return
        for i in range(1, n):
            for pattern in generate_patterns(n-i):
                yield (i, pattern[0] + i)

    for pattern in sorted(generate_patterns(S), key=lambda x: (x[0], x[1])):
        if is_appealing(pattern):
            output.append(f"{pattern[0]},{pattern[1]}")

    result = f"{S}:{'\n' + '\n'.join(output)}"
    return result

