def solution(stdin: str) -> str:
    common_word, E, *endings_lists, P, *phrases = stdin.split('\n')
    endings = {list(map(str.strip, line.split())) for line in endings_lists[:E]}
    endings_sets = {frozenset(s) for s in endings}

    def find_rhyming_phrase(phrase: str) -> bool:
        words = phrase.split()
        if len(words) == 2:
            ending = words[1][-1]
            return any(s.issuperset({ending}) for s in endings_sets)
        elif len(words) == 3:
            endings = [words[1][-1], words[2][-1]]
            return any(all(s.issuperset({e}) for e in endings) for s in endings_sets)
        return False

    output = '\n'.join('YES' if find_rhyming_phrase(phrase) else 'NO' for phrase in phrases[:P])
    return output


