def solution(stdin: str) -> str:
    input_list = stdin.split('\n')
    C, P = map(int, input_list[0].split())
    initial_heights = list(map(int, input_list[1].split()))

    def can_drop(dropped_piece: list, heights: list) -> bool:
        for col, height in enumerate(heights):
            for i in range(height, C):
                if dropped_piece[col] == 1 and heights[col] == i:
                    return False
        return True

    def get_rotations(piece: list) -> list:
        return [piece, [piece[0], piece[1], piece[2], piece[3], piece[0]], [piece[3], piece[0], piece[1], piece[2], piece[3]], [piece[2], piece[3], piece[0], piece[1], piece[2]]]

    def get_piece(P: int) -> list:
        if P == 1:
            return [0, 0, 0, 0, 1]
        elif P == 2:
            return [1, 0, 0, 0, 0]
        elif P == 3:
            return [0, 1, 0, 0, 0]
        elif P == 4:
            return [0, 0, 1, 0, 0]
        elif P == 5:
            return [0, 0, 0, 1, 0]
        elif P == 6:
            return [0, 0, 0, 0, 0]

    piece = get_piece(P)
    rotations = get_rotations(piece)
    dropped_pieces = []
    for rotation in rotations:
        dropped_piece = [0] * C
        for i in range(len(rotation)):
            if rotation[i] == 1:
                dropped_piece[i] = 1
        dropped_pieces.append(dropped_piece)

    def backtrack(heights: list, index: int) -> int:
        if index == len(dropped_pieces):
            return 1 if can_drop(dropped_pieces[-1], heights) else 0
        return backtrack(heights, index + 1) + backtrack(heights, index + 1)

    return backtrack(initial_heights, 0)

