def solution(stdin: str) -> str:
    input_list = stdin.split('\n')
    time = int(input_list[0].split()[0])
    N = int(input_list[0].split()[1])
    M = int(input_list[0].split()[2])

    grid = []
    for i in range(1, N + 1):
        grid.append(list(input_list[i]))

    min_zones = float('inf')
    start = None

    def is_valid(x, y, direction):
        if x < 0 or x >= N or y < 0 or y >= M:
            return False
        if grid[x][y] == '1' or (direction != 'R' and grid[x][y] == 'R') or (direction != 'U' and grid[x][y] == 'U') or (direction != 'D' and grid[x][y] == 'D') or (direction != 'L' and grid[x][y] == 'L'):
            return False
        return True

    for i in range(N):
        for j in range(M):
            if grid[i][j] == 'S':
                start = (i, j)
                break
        if start is not None:
            break

    if start is None:
        return "NOT POSSIBLE"

    directions = ['R', 'U', 'D', 'L']
    for direction in directions:
        x, y = start
        visited = set()
        while (is_valid(x, y, direction) and (x, y) not in visited):
            visited.add((x, y))
            if direction == 'R':
                x, y = x, y + 1
            elif direction == 'U':
                x, y = x - 1, y
            elif direction == 'D':
                x, y = x, y + 1
            elif direction == 'L':
                x, y = x + 1, y
            zones_count = len(visited)
            if zones_count % 2 == 0:
                min_zones = min(min_zones, zones_count)
            if zones_count == time:
                break

    return min_zones if min_zones != float('inf') else "NOT POSSIBLE"

