        if 0 <= x < width and 0 <= y < height and map_grid[y][x] != '#':
            return True
        return False

    def count_gold(x, y):
        count = 0
        if is_safe(x - 1, y) and map_grid[y][x - 1] == 'G':
            count += 1
        if is_safe(x + 1, y) and map_grid[y][x + 1] == 'G':
            count += 1
        if is_safe(x, y - 1) and map_grid[y - 1][x] == 'G':
            count += 1
        if is_safe(x, y + 1) and map_grid[y + 1][x] == 'G':
            count += 1
        return count

    safe_gold = 0
    x, y = map(lambda char: map_grid.index(char), ['P'])

    while True:
        gold_count = count_gold(x, y)
        if gold_count > 0:
            safe_gold += gold_count
            x, y = -1, -1
        else:
            if is_safe(x - 1, y):
                x -= 1
            elif is_safe(x + 1, y):
                x += 1
            elif is_safe(x, y - 1):
                y -= 1
