    def cool_down(slots: List[int], new_bottles: int) -> Tuple[int, int]:
        total_cold_bottles = sum(slots)
        extra_cold_bottles = max(0, new_bottles - total_cold_bottles)
        num_slots = min(new_bottles, len(slots))
