        num_slots, extra_cold_bottles = cool_down(slots, new_bottles)
        refill_scheme = [extra_cold_bottles] + [0] * (len(slots) - num_slots)
        for i in range(len(slots)):
            if i < num_slots:
                refill_scheme[i] += slots[i]
            else:
                refill_scheme[i] = slots[i]
