    def cdf(x, probabilities):
        cdf_value = 0.0
        for prob in probabilities:
            cdf_value += prob
            if cdf_value >= x:
                return prob
        return 1.0

    def expected_attempts(probabilities):
        total_attempts = 0.0
        for i in range(len(probabilities)):
            total_attempts += (1.0 - cdf(i / len(probabilities), probabilities)) * len(probabilities)
        return total_attempts

    def binary_search(probabilities, target_probability):
        left, right = 0, len(probabilities) - 1
        while left <= right:
            mid = (left + right) // 2
            if cdf(mid, probabilities) <= target_probability:
                left = mid + 1
            else:
                right = mid - 1
        return left

    def optimal_order(probabilities):
        total_attempts = expected_attempts(probabilities)
        for i in range(len(probabilities)):
            probabilities_without_i = probabilities[:i] + probabilities[i+1:]
            new_attempts = expected_attempts(probabilities_without_i)
            if new_attempts < total_attempts:
                total_attempts = new_attempts
        return total_attempts

