    n = int(input_list[0])
    distance_matrix = [[int(i) for i in input_list[j+1].split()] for j in range(n)]
    for i in range(n):
        for j in range(i+1, n):
            distance_matrix[i][j] = distance_matrix[j][i]

    def find_shortest_path(start: int, end: int) -> List[int]:
        visited = set()
        queue = [(start, [start])]
        while queue:
            node, path = queue.pop(0)
            for neighbor, cost in enumerate(distance_matrix[node]):
                if neighbor not in visited and cost > 0:
                    visited.add(neighbor)
                    queue.append((neighbor, path + [neighbor]))
                    if neighbor == end:
                        return path + [neighbor]
        return []

    result = []
    for i in range(1, n):
        for j in range(i+1, n):
            shortest_path = find_shortest_path(i, j)
            if shortest_path:
                result.append([shortest_path[0], shortest_path[-1]])

    return "\n".join([" ".join(map(str, road)) for road in result])

