    matrix = [list(map(int, stdin.split()[2:])) for _ in range(n)]

    def manhattan_distance(x, y):
        return abs(x[0] - y[0]) + abs(x[1] - y[1])

    def shortest_path(start, end):
        queue = [(start, 0)]
        visited = set()

        while queue:
            curr, dist = queue.pop(0)
            if curr == end:
                return dist
            if curr in visited:
                continue
            visited.add(curr)

            for neighbor in [(curr[0] + 1, curr[1]), (curr[0] - 1, curr[1]), (curr[0], curr[1] + 1), (curr[0], curr[1] - 1)]:
                if 1 <= neighbor[0] <= n and 1 <= neighbor[1] <= n:
                    queue.append((neighbor, dist + 1))

        return -1

    shortest = float('inf')
    for i in range(1, n + 1):
        for j in range(1, n + 1):
            if matrix[i - 1][j - 1] == k:
                dist = shortest_path((i, j), (1, 1))
                if dist != -1 and dist < shortest:
                    shortest = dist

    return str(shortest) if shortest != float('inf') else '-1'

