    def is_valid(r, c):
        return 1 <= r <= N and 1 <= c <= N and grid[r - 1][c - 1] != '#'

    def bfs(start_r, start_c):
        queue = [(start_r, start_c, 0)]
        visited = set()

        while queue:
            r, c, steps = queue.pop(0)

            if (r, c) == (1, 1):
                return steps

            for dr, dc in [(2, 1), (2, -1), (1, 2), (1, -2), (-2, 1), (-2, -1), (-1, 2), (-1, -2)]:
                r2, c2 = r + dr, c + dc
                if is_valid(r2, c2) and (r2, c2) not in visited:
                    visited.add((r2, c2))
                    queue.append((r2, c2, steps + 1))

        return -1

    start_r, start_c = 0, 0
    for r in range(N):
        for c in range(N):
            if grid[r][c] == 'K':
                start_r, start_c = r + 1, c + 1
                break
        if start_r != 0:
            break

    result = bfs(start_r, start_c)
    return str(result) if result != -1 else '-1'

