            encrypted_text += key_matrix[i // 2][0] + key_matrix[i // 2][0]
        else:
            row1, col1 = -1, -1
            row2, col2 = -1, -1
            for j in range(5):
                for k in range(5):
                    if key_matrix[j][k] == pair[0]:
                        row1, col1 = j, k
                    if key_matrix[j][k] == pair[1]:
                        row2, col2 = j, k
            if row1 == row2:
