def solution(stdin: str) -> str:
    input_list = stdin.split("\n")
    starting_coord = tuple(map(int, input_list[0].split()[::-1]))
    destination_coord = tuple(map(int, input_list[1].split()[::-1]))
    t = int(input_list[2])

    # Function to check if a path exists between two intersections
    def path_exists(start: tuple, end: tuple, t: int) -> bool:
        # Check if start and end are equal
        if start == end:
            return True

        # Check if t is less than or equal to 0
        if t <= 0:
            return False

        # Calculate distance between start and end
        distance = abs(start[0] - end[0]) + abs(start[1] - end[1])

        # If the distance is greater than t, return False
        if distance > t:
            return False

        # Otherwise, check the four adjacent intersections
        for i in range(-1, 2):
            for j in range(-1, 2):
                if i == 0 and j == 0:
                    continue
                adjacent_coord = (start[0] + i, start[1] + j)
                if adjacent_coord == end:
                    return True
                if path_exists(adjacent_coord, end, t - distance):
                    return True

        return False

    # Check if a path exists between starting and destination coordinates
    if path_exists(starting_coord, destination_coord, t):
        return "Y"
    else:
        return "N"

