    def spread_water(row, col):
        if grid[row][col] == 'V':
            return
        if row == 0 or col == 0 or row == N or col == M:
            return
        if grid[row-1][col] == 'V':
            grid[row][col] = 'V'
            spread_water(row, col-1)
            spread_water(row, col+1)
        elif grid[row-1][col] == '#':
            if grid[row][col-1] == 'V':
                grid[row][col] = 'V'
            if grid[row][col+1] == 'V':
                grid[row][col] = 'V'

    for i in range(N):
        for j in range(M):
            if grid[i][j] == 'V':
                spread_water(i, j)

    return '\n'.join([''.join(row) for row in grid])

