def solution(stdin: str) -> str:
    n = int(stdin.splitlines()[0])
    edges = [tuple(map(int, line.split())) for line in stdin.splitlines()[1:]]
    tree = [set() for _ in range(n + 1)]

    for a, b in edges:
        tree[a].add(b)
        tree[b].add(a)

    def dfs(node, visited, path):
        visited.add(node)
        path.append(node)

        for child in tree[node]:
            if child not in visited:
                dfs(child, visited, path)

        path.pop()
        visited.remove(node)

    max_edges = 0
    for i in range(1, n + 1):
        visited = set()
        path = []
        dfs(i, visited, path)

        for j in range(i + 1, n + 1):
            visited2 = set()
            path2 = []
            dfs(j, visited2, path2)

            visited3 = visited | visited2
            path3 = path + path2

            for k in range(1, n + 1):
                if k in visited3:
                    continue
                visited4 = visited3 | {k}
                path4 = path3 + [k]

                visited5 = visited4 | {k}
                path5 = path4[:]
                path5.append(k)

                edges_count = len(set(path4))
                if edges_count > max_edges:
                    max_edges = edges_count
                    a, b, c = i, j, k

    return str(max_edges) + "\n" + str(a) + " " + str(b) + " " + str(c)

