    def is_regular(brackets: str) -> bool:
        stack = []
        for b in brackets:
            if b == '(':
                stack.append(b)
            elif b == ')':
                if not stack or stack.pop() != '(':
                    return False
        return not stack

    def count_regular(brackets: str) -> int:
        count = 0
        stack = []
        for b in brackets:
            if b == '(':
                stack.append(b)
            elif b == ')':
                if not stack or stack.pop() != '(':
                    return 0
                count += 1
        return count

    result = 0
    for i in range(len(s) - 2*n + 1):
        brackets = s[i:i+2*n]
        if is_regular(brackets) and s[i:i+n] == s:
            result += count_regular(brackets)
            result %= (10**9 + 7)

    return result


