def solution(stdin: str) -> str:
    def is_palindromic(matrix: List[List[int]]) -> bool:
        return matrix == matrix[::-1][::] and all(matrix[i] == matrix[i][::-1] for i in range(len(matrix)))

    n = int(stdin.splitlines()[0])
    numbers = list(map(int, stdin.splitlines()[1].split()))

    if n == 1:
        matrix = [[numbers[0]]]
    elif n == 2:
        matrix = [[numbers[0], numbers[2]], [numbers[1], numbers[3]]]
    else:
        matrix = [[0] * n for _ in range(n)]
        numbers_left = numbers.copy()

        def backtrack(row: int, col: int) -> bool:
            if row == n:
                return is_palindromic(matrix)

            for i in range(n):
                if numbers_left and numbers_left[i] == matrix[row][col]:
                    continue

                matrix[row][col] = numbers_left.pop(i)
                if backtrack(row, col + 1):
                    return True
                numbers_left.insert(i, matrix[row][col])

            return False

        if not backtrack(0, 0):
            return False

    return "YES\n" + "\n".join(str(row) for row in matrix)

