    # Initialize the queue with the starting position (1, 1)
    queue = [(1, 1, 0)]
    while queue:
        # Pop the top element from the queue
        i, j, moves = queue.pop(0)
        # If we reach a square with N, return the number of moves
        if i * j == N:
            return str(moves)
        # Add the adjacent squares to the queue if they have not been visited
        if (i + 1, j) not in visited:
            queue.append((i + 1, j, moves + 1))
            visited.add((i + 1, j))
        if (i, j + 1) not in visited:
            queue.append((i, j + 1, moves + 1))
            visited.add((i, j + 1))
    # If we reach this point, it means there is no square containing N
    return "Impossible"

