            b = apply_segments(a, current_segments)
            current_diff = calculate_diff(b)
            if current_diff > max_diff:
                max_diff = current_diff
                best_b = b
                best_segments = [combo[i] for i in range(len(combo))]
        return max_diff, len(best_segments), best_segments

