def solution(stdin: str) -> str:
    n = int(stdin.splitlines()[0])
    a = list(map(int, stdin.splitlines()[1].split()))

    def find_blocks(a, n):
        blocks = []
        for i in range(n):
            for j in range(i + 1, n + 1):
                if sum(a[i:j]) == a[i] * (j - i):
                    blocks.append((i, j - 1))
                    break
        return blocks

    def is_valid(blocks):
        for i in range(len(blocks)):
            for j in range(i + 1, len(blocks)):
                if blocks[i][1] >= blocks[j][0]:
                    return False
        return True

    def find_maximum_blocks(blocks):
        blocks.sort(key=lambda x: x[1] - x[0])
        max_k = 1
        while True:
            max_k += 1
            if not is_valid(blocks[:max_k]):
                max_k -= 1
                break
        return max_k

    blocks = find_blocks(a, n)
    k = find_maximum_blocks(blocks)

    result = "\n".join([str(x[0]) + " " + str(x[1]) for x in blocks[:k]])
    return k, result

