            visited[node] = True
            for neighbor in adj[node]:
                if not visited[neighbor]:
                    if neighbor == stack[-1]:
                        return False
                    if dfs(neighbor, adj, visited, stack + [node]):
                        return True
            return False

        adj = [[] for _ in range(n)]
        for u, v in edges:
            adj[u - 1].append(v - 1)
            adj[v - 1].append(u - 1)

        for i in range(n):
            visited = [False] * n
            if not dfs(i, adj, visited, [i]):
                return False

        return True

