        def dfs(node: int, adj: List[List[int]], visited: List[bool], stack: List[int],
                  orient: List[int]) -> bool:
            visited[node] = True
            for neighbor in adj[node]:
                if not visited[neighbor]:
                    if neighbor == stack[-1]:
                        continue
                    if dfs(neighbor, adj, visited, stack + [node], orient):
                        return True
            return False

        adj = [[] for _ in range(n)]
        for u, v in edges:
            adj[u - 1].append(v - 1)
            adj[v - 1].append(u - 1)

        for i in range(n):
            visited = [False] * n
            if not dfs(i, adj, visited, [i], [0] * len(edges)):
                return "NO"

        return "YES" + "".join(map(str, orient))

