    def find_smallest_permutation(N: int, p: str, q: str) -> int:
        p = list(map(int, p.split()))
        q = list(map(int, q.split()))
        permutations = list(permutations_lexicographically_smaller_than(p, q))
        return abs(permutations.index(p) - permutations.index(q))

    def permutations_lexicographically_smaller_than(p: List[int], q: List[int]) -> List[List[int]]:
        def swap(p: List[int], i: int, j: int) -> None:
            p[i], p[j] = p[j], p[i]

        def generate_permutations(p: List[int]) -> List[List[int]]:
            if len(p) == 1:
                return [p]
            result = []
            for i in range(len(p)):
                new_p = p.copy()
                generate_permutations(new_p)
                result.extend([new_p[:i] + [p[0]] + new_p[i:] for new_p in result])
            return result

        def lexicographically_smaller(a: List[int], b: List[int]) -> bool:
            for i in range(1, len(a)):
                if a[i] < b[i]:
                    return True
                elif a[i] > b[i]:
                    return False
            return False

        permutations = generate_permutations(p)
        return [p for p in permutations if lexicographically_smaller(p, q)]

    def main(stdin: str) -> str:
        N = int(stdin.split()[0])
        p = stdin.split("\n")[1]
        q = stdin.split("\n")[2]
        result = find_smallest_permutation(N, p, q)
        return str(result)

    return main(stdin)

