        umbrellas.append((x, p))

    def find_optimal_strategy(current_umbrella_positions: list, remaining_umbrellas: list, current_fatigue: int, current_pos: int) -> int:
        if not remaining_umbrellas:
            return current_fatigue

        best_fatigue = float('inf')
        for i, (l, r) in enumerate(rain_segments):
            if l <= current_pos <= r and not current_umbrella_positions[i]:
                new_umbrella_positions = current_umbrella_positions.copy()
                new_umbrella_positions[i] = True
                new_current_fatigue = current_fatigue + umbrellas[i][1]
                new_current_pos = current_pos
                for j, (x, p) in enumerate(umbrellas):
                    if j != i:
                        new_umbrellas = remaining_umbrellas.copy()
                        new_umbrellas.remove((x, p))
                        new_current_fatigue = find_optimal_strategy(new_umbrella_positions, new_umbrellas, new_current_fatigue, new_current_pos)
                best_fatigue = min(best_fatigue, new_current_fatigue)

        return best_fatigue

    current_umbrella_positions = [False] * n
    current_fatigue = 0
    current_pos = 0
    remaining_umbrellas = [(x, p) for x, p in umbrellas]
    result = find_optimal_strategy(current_umbrella_positions, remaining_umbrellas, current_fatigue, current_pos)
    if result == float('inf'):
        return "-1"
    else:
        return str(result)


