    def dfs(graph: List[List[tuple]], visited: List[bool], mst: List[tuple], k: int) -> bool:
        if len(mst) == n - 1:
            return True
        u = visited.index(False)
        for v, w in graph[u]:
            if not visited[v]:
                if w <= k:
                    visited[v] = True
                    mst.append((u, v, w))
                    if dfs(graph, visited, mst, k):
                        return True
                    mst.pop()
                    visited[v] = False
        return False

    def unify_mst(edges: List[tuple], k: int) -> int:
        graph = [[] for _ in range(n)]
        for u, v, w in edges:
            graph[u].append((v, w))
            graph[v].append((u, w))

